import React from 'react'

function Product({data}) {
 
console.log(data);
  return (
    <div className='w-[15rem] h-[20rem] flex flex-col border '>
      <p>{data.title}</p>
      <p>{data.description.substring(1, 30)}</p>
      <img src={data.image} alt="" />
      <div>
        <p>{data.price}</p>
        <button>{
          true? "Add to cart" : "Remove From Cart"
        }</button>
      </div>
    </div>
  )
}

export default Product
